USE support;

CREATE TABLE IF NOT EXISTS appdomain ( 
	AppDomainId int(11) NOT NULL AUTO_INCREMENT, 
	DomainName varchar(255) NOT NULL, 
	DomainPrefix varchar(10) NOT NULL, 
	CreateDateTime__Gen DATETIME NOT NULL, 
	UpdateDateTime__Gen DATETIME NOT NULL, 
	PRIMARY KEY (AppDomainId), 
	UNIQUE INDEX applicationdomain_idx1 (DomainName) 
); 


DROP TABLE IF EXISTS routedefinition;

CREATE TABLE IF NOT EXISTS routedefinition (
    RouteDefinitionId int(11) NOT NULL AUTO_INCREMENT,
	RouteUrl varchar(255) NOT NULL,
	ControllerName varchar(255) NOT NULL,
	HttpMethod varchar(10) NOT NULL,
	IsPublic bit NOT NULL DEFAULT 0,
	IsAdminOnly bit NOT NULL DEFAULT 0,
	IsBasicAuth bit NOT NULL DEFAULT 0,
	IsBearerTokenAuth bit NOT NULL DEFAULT 0,
	RequiresReplayProtection bit NOT NULL DEFAULT 0,
	RequiresAuthorization bit NOT NULL DEFAULT 0,
	TableToAuthorize varchar(255) NULL,
	IsLoggingEnabled bit NOT NULL DEFAULT 0,
	CreateDateTime__Gen DATETIME NOT NULL,
    UpdateDateTime__Gen DATETIME NOT NULL,
	PRIMARY KEY (RouteDefinitionId),
	UNIQUE INDEX routedefinition_idx1 (RouteUrl, HttpMethod)
);

TRUNCATE TABLE routedefinition;

call rheem.AddColumnIfNotExists('support', 'routedefinition', 'AppDomainId',  'int NOT NULL');

call rheem.CreateForeignKeyIfNotExists('support', 'routedefinitionFK1', 'routedefinition', 'AppDomainId', 'appdomain', 'AppDomainId', 'dropAndReplace', 'ON DELETE CASCADE');		










































CREATE TABLE IF NOT EXISTS clientapplication ( 
	ClientApplicationId int(11) NOT NULL AUTO_INCREMENT, 
	ClientId varchar(255) NOT NULL, 
	BearerTokenProvider varchar(255) NOT NULL,  
	ApplicationName varchar(255) NOT NULL, 
	ProjectName varchar(255) NOT NULL,
	EmailAddress varchar(255) NOT NULL, 
	CreateDateTime__Gen DATETIME NOT NULL, 
	UpdateDateTime__Gen DATETIME NOT NULL, 
	PRIMARY KEY (ClientApplicationId),
	UNIQUE INDEX clientapplication_idx1 (ClientId) 
); 


CREATE TABLE IF NOT EXISTS clientapplicationappdomain ( 
	ClientApplicationAppDomainId int(11) NOT NULL AUTO_INCREMENT, 
	ClientApplicationId int NOT NULL, 
	AppDomainId int NOT NULL, 
	CreateDateTime__Gen DATETIME NOT NULL, 
	UpdateDateTime__Gen DATETIME NOT NULL, 
	PRIMARY KEY (ClientApplicationAppDomainId), 
	Foreign Key(ClientApplicationId) references clientapplication(ClientApplicationId) on delete cascade, 
	Foreign Key(AppDomainId) references appdomain(AppDomainId) on delete cascade, 
	UNIQUE INDEX clientapplicationappdomain_idx1 (ClientApplicationId, AppDomainId) 
);



 